import React, { Component } from "react";
import Chart from "react-apexcharts";
import ApexCharts from "apexcharts";
import { w3cwebsocket as W3CWebSocket } from "websocket";

const IP = "192.168.18.229:81";

class GraphWrapper extends Component {
  constructor(props) {
    super(props);

    this.state = {
      options: {
        chart: {
          id: "realtime",
          animations: {
            enabled: true,
            easing: "linear",
            dynamicAnimation: {
              speed: 1000
            }
          },
          toolbar: {
            show: false
          },
          zoom: {
            enabled: false
          }
        },
        dataLabels: {
          enabled: false
        },
        stroke: {
          curve: "smooth"
        },
        title: {
          text: "dynamic updating chart",
          align: "left"
        },
        markers: {
          size: 0
        },
        xaxis: {
          type: 'datetime',
          range: 10
        },
        yaxis: {
          min: 0
        },
        legend: {
          show: false
        }
      },
      series: [{ datas: [] }]
    };
  }

  componentDidMount() {
    const dataWS = new W3CWebSocket(`ws://${IP}`, ['arduino']);

    dataWS.onmessage = message => {
      if(message.data !== "Connected"){
        if(typeof message.data == "string") {
          let dataObj = {}
          const data = message.data.split(",")
          for(let val of data) {
            dataObj[val.split(":")[0]] = val.split(":")[1]
          }
          const x = Math.floor(new Date().getTime() / 1000);
          const y = dataObj['ph']
          let { datas } = this.state.series[0];
          datas.push({x,y})
          console.log(datas)
        }
      }
    }
    // this.updateInterval = setInterval(() => this.updateData(), 1000);
  }

  // updateData = () => {
  //   const x = Math.floor(new Date().getTime() / 1000);
  //   const y = Math.floor(Math.random() * 10);

  //   let { data } = this.state.series[0];
  //   data.push({ x,y });

  //   this.setState({ series: [{ data }] }, () =>
  //     ApexCharts.exec("realtime", "updateSeries", this.state.series)
  //   );
  // };

  render() {
    const { options, series } = this.state;

    return (
      <div className="mixed-chart">
        <Chart options={options} series={series} type="line" height="350" />
      </div>
    );
  }
}

export default GraphWrapper;
