import { useState, useEffect } from "react";
import { Layout, notification, Row, Col } from "antd";
import { w3cwebsocket as W3CWebSocket } from "websocket";

import Chart from "react-apexcharts";
import isEqual from "validator/lib/equals";
import isEmpty from "validator/lib/isEmpty";

import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";

import LoginComponent from "./components/Login"

import { formLogin, formLoginIsValid } from "./formdata/formLogin";

const EMAIL = "hydro@gmail.com";
const PASSWORD = "asdasd";
const IP = "192.168.18.229:81";
const optionChart = {
  chart: {
    height: 150,
    type: 'area',
    zoom: { enabled: false },
  },
  xaxis: {
    labels: {
      show: false
    },
    axisTicks: {
      show: false
    }
  },
  sparkline: { enabled: true },
  dataLabels: { enabled: false },
  stroke: { curve: 'smooth' },
  fill: { opacity: 1, },
}


const App = () => {
  const [login, setLogin] = useState(formLogin);
  const [isLogin, setIsLogin] = useState(false);
  const [statistic, setStatistic] = useState([ { suhu: "0", nutrisi: "0", ph: "0" } ]);

  const { email, password } = login;

  const inputChangeHandler = e => {
    const name = e.target.name
    const value = e.target.value

    if(isEmpty(value || "", { ignore_whitespace: true })){
      const data = {
        ...login,
        [name]: {
          ...login[name],
          value: value,
          isValid: false,
          message: "Kolom tidak boleh kosong",
        },
      };
      setLogin(data);
    } else {
      const data = {
        ...login,
        [name]: {
          ...login[name],
          value: value,
          isValid: true,
          message: null,
        },
      };
      setLogin(data);
    }
  }

  const submitLoginHandler = () => {
    if(formLoginIsValid(login, setLogin)){
      if(isEqual(email.value, EMAIL) && isEqual(password.value, PASSWORD)){
        notification.success({
          message: "Success",
          description: "Selamat datang Hydropronics",
        });
        setIsLogin(true);

        const dataWS = new W3CWebSocket(`ws://${IP}`, ['arduino']);

        dataWS.onmessage = message => {
          if(message.data !== "Connected"){
            if(typeof message.data == "string") {
              let dataObj = {}
              const data = message.data.split(",")
              for(let val of data) {
                dataObj[val.split(":")[0]] = val.split(":")[1]
              }
              setStatistic(data => [...data, dataObj])
            }
          }
        }

      } else {
        setIsLogin(false);
        notification.error({
          message: "Error",
          description: "Akun tidak terdaftar",
        });
      }
    }
  }

  let ph = statistic.map(data => data.ph)
  const suhu = statistic.map(data => data.suhu)
  const nutrisi = statistic.map(data => data.nutrisi)

  const seriesPH = [ { name: "PH", data: ph } ]

  const optionPH = {
    chart: {
      type: 'area',
      zoom: { enabled: false },
      toolbar: { show: false },
      animations: {
        enabled: true,
        easing: 'linear',
        dynamicAnimation: {
          speed: 1000
        }
      },

    },
    xaxis: {
      labels: {
        show: false
      },
      axisTicks: {
        show: false
      },
      tooltip: {
        enabled: false
      },
    },
    plotOptions: {
      bar: {
        columnWidth: '45%',
      }
    },
    sparkline: { enabled: true },
    dataLabels: { enabled: false },
    stroke: { curve: 'smooth' },
    fill: { opacity: 1, },

    title: {
      text: ph[ph.length - 1],
      offsetX: 30,
      style: {
        fontSize: '24px',
        cssClass: 'apexcharts-yaxis-title'
      }
    },
    subtitle: {
      text: 'PH',
      offsetX: 30,
      style: {
        fontSize: '14px',
        cssClass: 'apexcharts-yaxis-title'
      }
    },
    colors: ['#00E396'],
  }

  useEffect(() => {
    ph.shift()
  }, [ph])

  return(
    <>
      {isLogin ? (
        <Layout className="bg-white">
          <Layout.Content>
            <Container>
              <Row gutter={[16, 16]}>

                <Col lg={16} md={24} sm={24} xs={24}>
                  <Card className="mb-2">
                    <Chart options={optionPH} series={seriesPH} height={350} />
                  </Card>
                </Col>

                <Col lg={8} md={24} sm={24} xs={24}>
                  <Card className="mb-2">
                    <Card.Body>
                      <Card.Title>
                        PH
                      </Card.Title>
                      <h2>{ph[ph.length - 1]}</h2>
                    </Card.Body>
                  </Card>

                  <Card className="mb-2">
                    <Card.Body>
                      <Card.Title>
                        Suhu
                      </Card.Title>
                      <h2>{suhu[suhu.length - 1]}</h2>
                    </Card.Body>
                  </Card>

                  <Card className="mb-2">
                    <Card.Body>
                      <Card.Title>
                        Nutrisi
                      </Card.Title>
                      <h2>{nutrisi[nutrisi.length - 1]}</h2>
                    </Card.Body>
                  </Card>
                </Col>

              </Row>
            </Container>
          </Layout.Content>
        </Layout>
      ) : (
        <LoginComponent 
          state={login} 
          onChange={inputChangeHandler} 
          submit={submitLoginHandler} 
        />
      )}
    </>
  )
};

export default App;
