import { useState } from "react";
import { Layout, notification, Row, Col } from "antd";
import { w3cwebsocket as W3CWebSocket } from "websocket";

import Chart from "react-apexcharts";
import isEqual from "validator/lib/equals";
import isEmpty from "validator/lib/isEmpty";

import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";

import LoginComponent from "./components/Login"

import { formLogin, formLoginIsValid } from "./formdata/formLogin";

const EMAIL = "hydro@gmail.com";
const PASSWORD = "asdasd";
const IP = "192.168.18.229:81";
const optionChart = {
  chart: {
    height: 150,
    type: 'area',
    zoom: { enabled: false },
  },
  xaxis: {
    labels: {
      show: false
    },
    axisTicks: {
      show: false
    }
  },
  sparkline: { enabled: true },
  dataLabels: { enabled: false },
  stroke: { curve: 'smooth' },
  fill: { opacity: 1, },
}


const App = () => {
  const [login, setLogin] = useState(formLogin);
  const [isLogin, setIsLogin] = useState(false);
  const [statistic, setStatistic] = useState([ { suhu: "0", nutrisi: "0", ph: "0" } ]);

  const { email, password } = login;

  const inputChangeHandler = e => {
    const name = e.target.name
    const value = e.target.value

    if(isEmpty(value || "", { ignore_whitespace: true })){
      const data = {
        ...login,
        [name]: {
          ...login[name],
          value: value,
          isValid: false,
          message: "Kolom tidak boleh kosong",
        },
      };
      setLogin(data);
    } else {
      const data = {
        ...login,
        [name]: {
          ...login[name],
          value: value,
          isValid: true,
          message: null,
        },
      };
      setLogin(data);
    }
  }

  const submitLoginHandler = () => {
    if(formLoginIsValid(login, setLogin)){
      if(isEqual(email.value, EMAIL) && isEqual(password.value, PASSWORD)){
        notification.success({
          message: "Success",
          description: "Selamat datang Hydropronics",
        });
        setIsLogin(true);

        const dataWS = new W3CWebSocket(`ws://${IP}`, ['arduino']);

        dataWS.onmessage = message => {
          if(message.data !== "Connected"){
            if(typeof message.data == "string") {
              let dataObj = {}
              const data = message.data.split(",")
              for(let val of data) {
                dataObj[val.split(":")[0]] = val.split(":")[1]
              }
              setStatistic(data => [...data, dataObj])
            }
          }
        }

      } else {
        setIsLogin(false);
        notification.error({
          message: "Error",
          description: "Akun tidak terdaftar",
        });
      }
    }
  }

  const suhu = statistic.map(data => data.suhu)
  const ph = statistic.map(data => data.ph)
  const nutrisi = statistic.map(data => data.nutrisi)

  const seriesPH = [ { name: "PH", data: ph }, ]
  const seriesSuhu = [ { name: "Suhu", data: suhu }, ]
  const seriesNutrisi = [ { name: "Nutrisi", data: nutrisi}, ]

  const optionPH = {
  chart: {
    height: 150,
    type: 'area',
    zoom: { enabled: false },
  },
  xaxis: {
    labels: {
      show: false
    },
    axisTicks: {
      show: false
    }
  },
  sparkline: { enabled: true },
  dataLabels: { enabled: false },
  stroke: { curve: 'smooth' },
  fill: { opacity: 1, },
    title: {
      text: ph[ph.length - 1],
      offsetX: 30,
      style: {
        fontSize: '24px',
        cssClass: 'apexcharts-yaxis-title'
      }
    },
    subtitle: {
      text: 'PH',
      offsetX: 30,
      style: {
        fontSize: '14px',
        cssClass: 'apexcharts-yaxis-title'
      }
    },
    colors: ['#00E396'],
  }

  const optionSuhu = {
    ...optionChart,
    title: {
      text: suhu[suhu.length - 1],
      offsetX: 30,
      style: {
        fontSize: '24px',
        cssClass: 'apexcharts-yaxis-title'
      }
    },
    subtitle: {
      text: 'Suhu',
      offsetX: 30,
      style: {
        fontSize: '14px',
        cssClass: 'apexcharts-yaxis-title'
      }
    },
    colors: ['#008FFB'],
  }

  const optionNutrisi = {
    ...optionChart,
    title: {
      text: nutrisi[nutrisi.length - 1],
      offsetX: 30,
      style: {
        fontSize: '24px',
        cssClass: 'apexcharts-yaxis-title'
      }
    },
    subtitle: {
      text: 'Nutrisi',
      offsetX: 30,
      style: {
        fontSize: '14px',
        cssClass: 'apexcharts-yaxis-title'
      }
    },
    colors: ['#e67e22'],
  }

  return(
    <>
      {isLogin ? (
        <Layout className="bg-white">
          <Layout.Content>
            <Container fluid>
              <Row gutter={[16, 16]}>

                <Col lg={24} md={24} sm={24} xs={24}>
                  <Card>
                    <Chart options={optionPH} series={seriesPH} />
                  </Card>
                </Col>

                <Col lg={8} md={24} sm={24} xs={24}>
                  <Card>
                    <Chart options={optionPH} series={seriesPH} />
                  </Card>
                </Col>

                <Col lg={8} md={24} sm={24} xs={24}>
                  <Card>
                    <Chart options={optionSuhu} series={seriesSuhu} />
                  </Card>
                </Col>

                <Col lg={8} md={24} sm={24} xs={24}>
                  <Card>
                    <Chart options={optionNutrisi} series={seriesNutrisi} />
                  </Card>
                </Col>

              </Row>
            </Container>
          </Layout.Content>
        </Layout>
      ) : (
        <LoginComponent 
          state={login} 
          onChange={inputChangeHandler} 
          submit={submitLoginHandler} 
        />
      )}
    </>
  )
};

export default App;
