import { useState } from "react";
import { Layout, notification } from "antd";
import { w3cwebsocket as W3CWebSocket } from "websocket";

import Container from "react-bootstrap/Container";
import isEqual from "validator/lib/equals";
import isEmpty from "validator/lib/isEmpty";

import LoginComponent from "./components/Login"

import { formLogin, formLoginIsValid } from "./formdata/formLogin";

const EMAIL = "hydro@gmail.com";
const PASSWORD = "asdasd";
const IP = "192.168.18.229:81";

const App = () => {
  const [login, setLogin] = useState(formLogin);
  const [isLogin, setIsLogin] = useState(false);
  const [statistic, setStatistic] = useState([ { suhu: "0", nutrisi: "0", ph: "0" } ]);

  const { email, password } = login;

  const inputChangeHandler = e => {
    const name = e.target.name
    const value = e.target.value

    if(isEmpty(value || "", { ignore_whitespace: true })){
      const data = {
        ...login,
        [name]: {
          ...login[name],
          value: value,
          isValid: false,
          message: "Kolom tidak boleh kosong",
        },
      };
      setLogin(data);
    } else {
      const data = {
        ...login,
        [name]: {
          ...login[name],
          value: value,
          isValid: true,
          message: null,
        },
      };
      setLogin(data);
    }
  }

  const submitLoginHandler = () => {
    if(formLoginIsValid(login, setLogin)){
      if(isEqual(email.value, EMAIL) && isEqual(password.value, PASSWORD)){
        notification.success({
          message: "Success",
          description: "Selamat datang Hydropronics",
        });
        setIsLogin(true);

        const dataWS = new W3CWebSocket(`ws://${IP}`, ['arduino']);

        dataWS.onmessage = message => {
          if(message.data !== "Connected"){
            if(typeof message.data == "string") {
              let dataObj = {}
              const data = message.data.split(",")
              for(let val of data) {
                dataObj[val.split(":")[0]] = val.split(":")[1]
              }
              setStatistic(data => [...data, dataObj])
            }
          }
        }

      } else {
        setIsLogin(false);
        notification.error({
          message: "Error",
          description: "Akun tidak terdaftar",
        });
      }
    }
  }

  const ph = statistic.map(data => data.ph)
  const nutrisi = statistic.map(data => data.nutrisi)

  const series = [
    { 
      name: "PH",
      data: ph
    }, 
    {
      name: "Nutrisi",
      data: nutrisi
    }
  ]

  const option = {
    chart: {
      height: 350,
      type: 'area'
    },
    dataLabels: {
      enabled: false
    },
    stroke: {
      curve: 'smooth'
    },
    xaxis: {
      type: 'datetime',
      categories: ["2018-09-19T00:00:00.000Z", "2018-09-19T01:30:00.000Z", "2018-09-19T02:30:00.000Z", "2018-09-19T03:30:00.000Z", "2018-09-19T04:30:00.000Z", "2018-09-19T05:30:00.000Z", "2018-09-19T06:30:00.000Z"]
    },
    tooltip: {
      x: {
        format: 'dd/MM/yy HH:mm'
      },
    },
  }

  return(
    <>
      {isLogin ? (
        <Layout className="bg-white">
          <Layout.Content>
            <Container>

            </Container>
          </Layout.Content>
        </Layout>
      ) : (
        <LoginComponent 
          state={login} 
          onChange={inputChangeHandler} 
          submit={submitLoginHandler} 
        />
      )}
    </>
  )
};

export default App;
