import { useEffect, useState } from "react";
import { Layout, notification, Row, Col, Switch } from "antd";
import { w3cwebsocket as W3CWebSocket } from "websocket";

import Chart from "react-apexcharts";
import ApexCharts from "apexcharts";
import isEqual from "validator/lib/equals";
import isEmpty from "validator/lib/isEmpty";

import Nav from "react-bootstrap/Nav";
import Card from "react-bootstrap/Card";
import Navbar from "react-bootstrap/Navbar";
import Container from "react-bootstrap/Container";

import LoginComponent from "./components/Login"

import { formLogin, formLoginIsValid } from "./formdata/formLogin";

const EMAIL = "hydro@gmail.com";
const PASSWORD = "asdasd";
const IP = "192.168.18.229:81";

const App = () => {
  const [login, setLogin] = useState(formLogin);
  const [isLogin, setIsLogin] = useState(false);
  const [statistic, setStatistic] = useState([ { suhu: "0", nutrisi: "0", ph: "0" } ]);
  const [series, setSeries] = useState([ { data: [] } ])

  const { email, password } = login;

  const inputChangeHandler = e => {
    const name = e.target.name
    const value = e.target.value

    if(isEmpty(value || "", { ignore_whitespace: true })){
      const data = {
        ...login,
        [name]: {
          ...login[name],
          value: value,
          isValid: false,
          message: "Kolom tidak boleh kosong",
        },
      };
      setLogin(data);
    } else {
      const data = {
        ...login,
        [name]: {
          ...login[name],
          value: value,
          isValid: true,
          message: null,
        },
      };
      setLogin(data);
    }
  }

  const submitLoginHandler = () => {
    if(formLoginIsValid(login, setLogin)){
      if(isEqual(email.value, EMAIL) && isEqual(password.value, PASSWORD)){
        notification.success({
          message: "Success",
          description: "Selamat datang Hydropronic",
        });
        setIsLogin(true);

        const dataWS = new W3CWebSocket(`ws://${IP}`, ['arduino']);

        dataWS.onmessage = message => {
          if(message.data !== "Connected"){
            if(typeof message.data == "string") {
              let dataObj = {}
              const dataMsg = message.data.split(",")
              for(let val of dataMsg) {
                dataObj[val.split(":")[0]] = val.split(":")[1]
              }
              setStatistic(data => [...data, dataObj])
              const x = Math.floor(new Date().getTime() / 1000);
              const y = dataObj['ph']
              let { data } = series[0];
              data.push({x,y})
            }
          }
        }

      } else {
        setIsLogin(false);
        notification.error({
          message: "Error",
          description: "Akun tidak terdaftar",
        });
      }
    }
  }

  useEffect(() => {
    const updateInterval = setInterval(() => updateData(), 1000)
    return () => clearInterval(updateInterval)
  }) 

  const updateData = () => {
    let { data } = series[0];
    setSeries([{ data }], () =>
      ApexCharts.exec("realtime", "updateSeries", series)
    );
  }

  let ph = statistic.map(data => parseFloat(data.ph))
  const suhu = statistic.map(data => data.suhu)
  const nutrisi = statistic.map(data => data.nutrisi)
  const seriesPH = [ { name: "PH", data: ph } ]

  const optionPH = {
    chart: {
      id: 'realtime',
      type: 'area',
      zoom: { enabled: false },
      toolbar: { show: false },
      animations: {
        enabled: true,
        easing: 'linear',
        dynamicAnimation: {
          speed: 1000
        }
      },
    },
    yaxis: { min: 0 },
    sparkline: { enabled: true },
    dataLabels: { enabled: false },
    xaxis: {
      type: 'datetime',
      range: 10,
      labels: { show: false },
      axisTicks: { show: false },
      tooltip: { enabled: false },
    },
    plotOptions: {
      bar: { columnWidth: '45%', }
    },
    stroke: { 
      curve: 'smooth', 
      width: 3,
    },
    title: {
      text: parseFloat(ph[ph.length - 1]) + ' pH',
      offsetX: 30,
      style: {
        fontSize: '24px',
        cssClass: 'display-4'
      }
    },
    subtitle: {
      text: 'Power of Hydrogen',
      offsetX: 30,
      style: {
        fontSize: '14px',
        cssClass: 'apexcharts-yaxis-title'
      }
    },
    colors: ['#54a0ff'],
  }

  const options = {
    chart: {
      id: "realtime",
      height: 350,
      type: 'line',
      animations: {
        enabled: true,
        easing: "linear",
        dynamicAnimation: {
          speed: 1000
        }
      },
      toolbar: {
        show: false
      },
      zoom: {
        enabled: false
      }
    },
    dataLabels: {
      enabled: false
    },
    stroke: {
      curve: "smooth"
    },
    title: {
      text: "dynamic updating chart",
      align: "left"
    },
    markers: {
      size: 0
    },
    xaxis: {
      type: 'datetime',
      range: 10
    },
    yaxis: { min: 0 },
    legend: { show: false }
  }


  const phValue = parseFloat(ph[ph.length - 1])
  const isOnPHUp = phValue < 5
  const isOnPHDown = phValue > 7
  const isOnNutrisi = nutrisi[nutrisi.length - 1] < 500

  useEffect(() => {
    if(isLogin){
      if(isOnPHUp){
        notification.success({
          message: "Pompa pH up",
          description: "Pompa pH up dihidupkan",
        });
      } else {
        notification.success({
          message: "Pompa pH up",
          description: "Pompa pH up dimatikan",
        });
      }
    }

  },[isOnPHUp, isLogin ])

  useEffect(() => {
    if(isLogin){
      if(isOnPHDown){
        notification.success({
          message: "Pompa pH down",
          description: "Pompa pH down dihidupkan",
        });
      } else {
        notification.success({
          message: "Pompa pH down",
          description: "Pompa pH down dimatikan",
        });
      }
    }
  },[isOnPHDown, isLogin])

  useEffect(() => {
    if(isLogin){
      if(isOnNutrisi){
        notification.success({
          message: "Pompa nutrisi",
          description: "Pompa nutrisi dihidupkan",
        });
      } else {
        notification.success({
          message: "Pompa nutrisi",
          description: "Pompa nutrisi dimatikan",
        });
      }
    }
  },[isOnNutrisi, isLogin])

  return(
    <>
      {isLogin ? (
        <>
        <Navbar expand="lg" variant="light" bg="light">
          <Container>
            <Navbar.Brand className="font-weight-bold">HYDRO X TECH</Navbar.Brand>
            <Nav className="ml-auto">
              <Nav.Link onClick={() => setIsLogin(false)}>Logout</Nav.Link>
            </Nav>
          </Container>
        </Navbar>
        <Layout className="bg-white mt-5">
          <Layout.Content>
            <Container>
              <Row gutter={[16, 16]}>

                <Col lg={16} md={24} sm={24} xs={24}>
                  <Card className="mb-2 h-100 shadow">
                    <Chart options={options} series={series} type="line" height="350" />
                  </Card>
                </Col>

                <Col lg={16} md={24} sm={24} xs={24}>
                  <Card className="mb-2 h-100 shadow">
                    <Chart options={optionPH} series={seriesPH} height={428} />
                  </Card>
                </Col>

                <Col lg={8} md={24} sm={24} xs={24}>
                  <Card className="mb-3 shadow">
                    <Card.Body className="p-2 h-100">
                      <h5>Suhu</h5>
                      <h2 className="text-center display-4">{suhu[suhu.length - 1]}Â°C</h2>
                    </Card.Body>
                  </Card>

                  <Card className="mb-3 shadow">
                    <Card.Body className="p-2">
                      <h5>Nutrisi</h5>
                      <h2 className="text-center display-4">{nutrisi[nutrisi.length - 1]} ppm</h2>
                    </Card.Body>
                  </Card>

                  <Card className="shadow">
                    <Card.Body className="p-2 text-center">
                      <h5 className="text-left">Pompa</h5>
              <p>pH Up: <Switch checkedChildren="On" unCheckedChildren="Off"  checked={parseFloat(ph[ph.length - 1]) < 5} /></p> 
              <p>pH Down: <Switch checkedChildren="On" unCheckedChildren="Off"  checked={parseFloat(ph[ph.length - 1]) > 7} /></p> 
              <p>Nutrisi: <Switch checkedChildren="On" unCheckedChildren="Off"  checked={nutrisi[nutrisi.length - 1] < 500} /></p> 
                    </Card.Body>
                  </Card>
                </Col>

              </Row>
            </Container>
          </Layout.Content>
        </Layout>
        </>
      ) : (
        <LoginComponent 
          state={login} 
          onChange={inputChangeHandler} 
          submit={submitLoginHandler} 
        />
      )}
    </>
  )
};

export default App;
